    public boolean cekKosong(String inputan){
        if(inputan.equals("")){
            return true;
        }else
            return false;
    }
    
    public boolean cekOver(int digit, int batas){
        if(digit>batas){
            return true;
        }else
            return false;
    }
    
    private void simpanButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_simpanButtonActionPerformed
        // TODO add your handling code here:
        String nim = nimTextField.getText();
        int digitNIM = nimTextField.getText().length();
        String fname = fnameTextField.getText();
        String lname = lnameTextField.getText();
        String name = fname+" "+lname;
        int digitNama = fname.length()+lname.length();
        String umur = umurTextField.getText();
        int digitUmur = umurTextField.getText().length();
        String asal = asalComboBox.getSelectedItem().toString();
        boolean validasiKosong = (cekKosong(nim)||cekKosong(fname)||cekKosong(lname)||cekKosong(umur));
        boolean validasiDigit = (cekOver(digitNIM,6)||cekOver(digitUmur,3)||cekOver(digitNama,50));
        boolean invalid =  (validasiKosong||validasiDigit);
        
        //Cek kosong
        if(validasiKosong){
            if(cekKosong(nim)){
                JOptionPane.showMessageDialog(null, "NIM wajib diisi!");
                //System.out.println("Terjadi kesalahan input: NIM kosong");
            }
            if(cekKosong(fname)){
                JOptionPane.showMessageDialog(null, "Nama depan wajib diisi!");
                //System.out.println("Terjadi kesalahan input: Nama depan kosong");
            }
            if(cekKosong(lname)){
                JOptionPane.showMessageDialog(null, "Nama belakang wajib diisi!");
                //System.out.println("Terjadi kesalahan input: Nama belakang kosong");
            }
        
            if(cekKosong(umur)){
                JOptionPane.showMessageDialog(null, "Umur wajib diisi!");
                //System.out.println("Terjadi kesalahan input: Umur kosong");
            }
        }
        
        //Cek digit
        if(validasiDigit){
            if(cekOver(digitNIM,6)){
                JOptionPane.showMessageDialog(null, "Terjadi kesalahan input: NIM lebih dari 6 digit");
                System.out.println("Digit NIM: " + digitNIM);
            }
            if(cekOver(digitUmur,3)){
                JOptionPane.showMessageDialog(null, "Terjadi kesalahan input: Umur Anda tidak wajar");
                System.out.println("Digit umur: " + digitUmur);
            }
            if(cekOver(digitNama,50)){
                JOptionPane.showMessageDialog(null, "Terjadi kesalahan input: Nama lebih dari 50 karakter");
                System.out.println("Jumlah karakter: " + digitNama);
            }
        }
        if(invalid==true){
            System.out.println("Data tidak valid");
        }
        else{
            System.out.println("Data valid");
            System.out.println("Daftar NIM dan Nama Mahasiswa yang telah berhasil disimpan :");
            mhs.tambahMahasiswa(new Mahasiswa(nim, name, umur, asal));
            mhs.tampilMahasiswa();
        }
    }//GEN-LAST:event_simpanButtonActionPerformed

