    private void umurTextFieldKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_umurTextFieldKeyPressed
        // TODO add your handling code here:
        char c = evt.getKeyChar();
        int digitUmur = umurTextField.getText().length() + 1;
        if(Character.isLetter(c)){
            umurTextField.setEditable(false);
            umurErrorLabel.setText("Silakan input Angka");
        }
        else{
            umurTextField.setEditable(true);
            if (digitUmur>3){
                umurErrorLabel.setText("Anda terlalu tua");
            }
            else{
             umurErrorLabel.setText("");
            }
        }
    }//GEN-LAST:event_umurTextFieldKeyPressed

