    private void nimTextFieldKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_nimTextFieldKeyPressed
        // TODO add your handling code here:
        char c = evt.getKeyChar();
        int digit = nimTextField.getText().length() + 1;
        if(Character.isLetter(c)){
            nimTextField.setEditable(false);
            nimErrorLabel.setText("Silakan input Angka");
        }else{
            nimTextField.setEditable(true);
            if (digit>6){
                nimErrorLabel.setText("NIM harus terdiri dari 6 digit");
            }else{
                nimTextField.setEditable(true);
                nimErrorLabel.setText("");
            }
        }
        
    }//GEN-LAST:event_nimTextFieldKeyPressed

